/*文章內文 */
import styled from 'styled-components';
import backgroundImg from '../img/background.PNG';

const Page = styled.div`
    height: 100%;
    padding: 0;
    margin: 0;
`;

const ArticleDetailAuthor = styled.h3`
    padding-top:10px;
    padding-left:20px
`;

const ArticleDetailText = styled.h2`
    padding-top:10px;
    text-align:center;
`;



export {ArticleDetailAuthor, ArticleDetailText};
